"use strict";(self.webpackChunkblog=self.webpackChunkblog||[]).push([[9450],{6029:function(e){e.exports=JSON.parse('{"blogPosts":[{"id":"the-19days-before-robomaster","metadata":{"permalink":"/the-19days-before-robomaster","editUrl":"https://github.com/pansyhou/pansyhou.github.io/edit/main/blog/talk/\u8d5b\u524d19\u5929.md","source":"@site/blog/talk/\u8d5b\u524d19\u5929.md","title":"\u8d5b\u524d19\u5929\u778ebb","description":"\u5f88\u4e45\u90fd\u6ca1\u6709\u5199\u8fc7\u65e5\u5fd7\u4e86\uff0c\u662f\u65f6\u5019\u8bf4\u8bf4\u81ea\u5df1\u7684\u5fc3\u91cc\u8bdd\u4e86\uff0c\u6709\u8bdd\u8bf4\u4e0d\u51fa\uff0c\u4e0d\u77e5\u9053\u8bf4\u7ed9\u8c01\uff0c\u8bf4\u4e86\u53c8\u4e0d\u77e5\u9053\uff0c\u6ca1\u529e\u6cd5\u5b89\u6170\u6211\u7684\u3002","date":"2023-05-01T00:00:00.000Z","formattedDate":"2023\u5e745\u67081\u65e5","tags":[{"label":"\u968f\u7b14","permalink":"/tags/\u968f\u7b14"},{"label":"\u6742\u8c08","permalink":"/tags/\u6742\u8c08"},{"label":"RoboMaster","permalink":"/tags/robo-master"}],"readingTime":2.9033333333333333,"hasTruncateMarker":true,"authors":[{"name":"pansyhou","title":"embedded engineer / \u5b66\u751f","url":"https://github.com/pansyhou","imageURL":"/img/logo.webp","key":"pansyhou"}],"frontMatter":{"slug":"the-19days-before-robomaster","title":"\u8d5b\u524d19\u5929\u778ebb","date":"2023-05-1","authors":"pansyhou","tags":["\u968f\u7b14","\u6742\u8c08","RoboMaster"],"keywords":["\u968f\u7b14","\u6742\u8c08","RoboMaster"]},"nextItem":{"title":"\u9ea6\u514b\u7eb3\u59c6\u8f6e\u8fd0\u52a8\u5206\u89e3","permalink":"/Decomposition-of-mecanum-wheel-motion"}},"content":"\u5f88\u4e45\u90fd\u6ca1\u6709\u5199\u8fc7\u65e5\u5fd7\u4e86\uff0c\u662f\u65f6\u5019\u8bf4\u8bf4\u81ea\u5df1\u7684\u5fc3\u91cc\u8bdd\u4e86\uff0c\u6709\u8bdd\u8bf4\u4e0d\u51fa\uff0c\u4e0d\u77e5\u9053\u8bf4\u7ed9\u8c01\uff0c\u8bf4\u4e86\u53c8\u4e0d\u77e5\u9053\uff0c\u6ca1\u529e\u6cd5\u5b89\u6170\u6211\u7684\u3002\\n\\n\u6b63\u597d\u8fd9\u4e0d\u662f\u4e0a\u7ebf\u4e86\u4e2a\u535a\u5ba2\u561b\uff0c\u968f\u624b\u5199\u4e00\u4e0b\u5427\u3002\\n\\n\x3c!-- truncate --\x3e\\n\\n## \u6b63\u9898\\n\\n\u8001\u5b9e\u8bb2\u6211\u73b0\u5728\u5e94\u8be5\u5728\u5b9e\u9a8c\u5ba4\u966a\u7740\u4ed6\u4eec\u52a0\u73ed\u7684\uff0c\u4f46\u662f\uff0c\u6108\u6765\u6108\u89c9\u5f97\uff0c\u7d2f\\n\\n\u5f88\u591a\u65f6\u5019\u6211\u5446\u5728\u90a3\u90fd\u662f\u5e72\u5176\u4ed6\u7684\u6d3b\uff0c\u6216\u8005\u8bf4\u534f\u8c03\u6216\u8005\u8bf4\u95f2\u804a\uff0c\u6ca1\u591a\u5c11\u65f6\u95f4\u662f\u4e13\u6ce8\u4e8e\u81ea\u5df1\u7684\u5de5\u4f5c\u4e0a\u7684\\n\\n\u4e3a\u4ec0\u4e48\u5927\u4e8c\u5c31\u60f3\u5f53\u7ba1\u7406\u5c42\uff0c\u56e0\u4e3a\u5927\u4e00\u89c9\u5f97\u7ba1\u7406\u5c42\u6709\u95ee\u9898\uff0c\u4f60\u5982\u679c\u80fd\u627e\u5230\u5f53\u65f6\u7684\u6211\uff0c\u6211\u53ef\u80fd\u6709\u4e00\u5806\u60f3\u5410\u69fd\u7684\uff0c\u5e74\u8f7b\u6c14\u76db\u561b\\n\\n\u4f46\u662f\u73b0\u5728\u7684\u6211\uff0c\u611f\u89c9\u6709\u70b9\u75b2\u60eb\u4e0d\u582a\u4e86\u3002\\n\\n\u4e5f\u8bb8\u53bb\u4e0d\u4e86\u6b63\u5f0f\u8d85\u6297\u662f\u5bf9\u6211\u7684\u4ec1\u6148\u5427\uff0c\u4f46\u662f\u8fd8\u662f\u8981\u82b1\u4e00\u7b14\u94b1\uff08\u8fd8\u662f\u5bf9\u6211\u6709\u6240\u8003\u9a8c\uff09\\n\\n\u5230\u73b0\u5728\u5de5\u7a0b\u624d\u51fa\u4e86\u8f66\uff0c\u800c\u4e14\u8fd8\u6ca1\u5b8c\u5168\u51fa\u597d\uff0c\u4f60\u8ba9\u7535\u63a7\u89c6\u89c9\u600e\u4e48\u8c03\\n\\n\u4eba\u624b\u4e0d\u8db3\u561b \\n\\n\u662f\uff0c\u6211\u627f\u8ba4\uff0c\u4eca\u5e74\u4eba\u771f\u5fc3\u4e0d\u591f\uff0c\u5f88\u591a\u65f6\u5019\u90fd\u662f\u68af\u961f\u9876\u4e0a\u6765\u7684\uff0c\u602a\u4e0d\u5f97\u90a3\u65f6\u5019\u5a77\u5a77\u5c31\u662f\u6b63\u5f0f\u961f\u5458\uff0c\u56e0\u4e3a\u771f\u5fc3\u4eba\u624b\u4e0d\u591f\uff0c\u5f53\u65f6\u5c31\u5df2\u7ecf\u8865\u62db\u4e86\u3002\u4f46\u662f \u6211\u4eec\u6ca1\u6709\u3002\u5fd8\u5566\\n\\n\\n\\n\u4e0d\u4ec5\u662f\u8fd9\u4e2a\u7684\u95ee\u9898\uff0c\u94b1\u4e5f\u662f\u4e2a\u5927\u95ee\u9898\u3002\\n\\n\u8d85\u6297\u53bb\u957f\u6c99\u89811.5k\uff0c\u5982\u679c\u5e26\u591a\u70b9\u68af\u961f\u7684\u8bdd\u94b1\u80af\u5b9a\u8981\u66f4\u591a\\n\\n\u6ca1\u94b1\u4e86...\u5927\u5b66\u751f\uff08\u672c\u8eab\u6211\u81ea\u5df1\u5c31\u7231\u82b1\u94b1\uff0c\u5e73\u65f6\u70b9\u591a\u4e2a\u5bb5\u591c\u5565\u7684\uff09\\n\\n\u957f\u6c99\u8981\u82b11.5k\u554a\u54e5\uff0c\u4e0d\u5c11\u7684\u652f\u51fa\u3002\u3002\u3002\u4e0d\u597d\u548c\u7238\u5988\u5f00\u53e3\uff0c\u5f00\u4e86\u4e5f\u4e0d\u77e5\u9053\u600e\u4e48\u638f\u7684\u51fa\u6765\\n\\n\u600e\u4e48\u8d5a\u5916\u5feb\uff1f\\n\\n\u4ee5\u524d\u603b\u8c03\u4f83\u81ea\u5df1\uff0c\u5b66\u5b8cstm32\u52a0\u4e0arm\uff0c\u80fd\u5728\u5d4c\u5165\u5f0f\u6a2a\u7740\u8d70\uff0c\u6211\u770b\u672a\u5fc5\\n\\n\u6211\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u600e\u4e48\u627e\u5916\u5feb\\n\\n\u96be\u53d7\\n\\n> \u534e\u4e3d\u7684\u8f6c\u8eab \uff1a\\n>\\n> \u4e0d\u662f\u719f\u4eba\u4ecb\u7ecd\uff0c\u4f60\u4f1a\u611f\u89c9\u5d29\u6e83\u7684\u3002\u641e\u4e0d\u597d\u8fde\u94b1\u90fd\u62ff\u4e0d\u5230\u3002\u5f88\u9ebb\u70e6\u7684\u3002\\n>\\n> \u7a7f\u8d8a\u65f6\u7a7a\uff1a\\n>\\n> \u8fc7\u6765\u4eba\uff0c\u4e0d\u662f\u719f\u4eba\u662f\u4e0d\u662f\u94b1\u5c11\u4e8b\u591a\u3002\\n>\\n> \u7a7f\u8d8a\u65f6\u7a7a\uff1a\\n>\\n> \u8fd0\u6c14\u7279\u4e0d\u597d\uff0c\u8fd8\u662f\u50cf\u8c08\u604b\u7231\u4e2d\u7684\uff0c\u4f60\u662f\u597d\u4eba\u3002\\n\\n\u611f\u8c22\u7238\u5988\u7ed9\u4e86\u6211\u8fd9\u4e48\u591a\u652f\u6301\uff0c\u8981\u4e0d\u662f\u4ed6\u4eec\u5fc3\u7406\u7d20\u8d28\u5f3a\u5927\uff0c\u6297\u4f4f\u4e86\u8fd9\u7247\u5929\uff0c\u6211\u53ef\u80fd\u65e9\u70c2\u4e86\\n\\n\\n\\n\u6700\u540e\u7684\u6700\u540e\u8fd8\u662f\u8981\u8bf4\u4e00\u4e0b\u611f\u60c5\\n\\n51\u8fd9\u51e0\u5929\uff0c\u5929\u5929\u6709\u4eba\u5728\u53d1\u670b\u53cb\u5708\uff0c\u53bb\u54ea\u73a9\u53bb\u54ea\u73a9\uff0c\u53bb\u89c1\u54ea\u5bb6\u7684\u5973\u670b\u53cb\\n\\n\u8001\u5b9e\u8bb2\u8fd8\u662f\u633a\u538b\u6291\u7684\u3002\\n\\n\u4ece\u5927\u4e00\u8fdb\u5165\u5b9e\u9a8c\u5ba4\u4ee5\u6765\uff0c\u8981\u4e48\u5728\u5b9e\u9a8c\u5ba4\uff0c\u8981\u4e48\u5728\u4e0a\u8bfe\uff0c\u8981\u4e48\u5728\u5bbf\u820d\\n\\n\u54ea\u6765\u7684\u4ec0\u4e48\u65c5\u6e38\uff0c\u611f\u60c5\\n\\n\u771f\u597d\u7b11\uff0c\u8fd9\u4e24\u5e74\u6ca1\u4ec0\u4e48\u4ea7\u51fa\uff0c\u4e5f\u6ca1\u5b66\u4ec0\u4e48\uff0c\u4e0d\u6127\u662f\u540a\u513f\u90ce\u5f53\u5c0f\u5b50\\n\\n\u8fd8\u65c5\u6e38\uff0c\u8fd8\u611f\u60c5\\n\\n\u603b\u611f\u89c9\uff0c\u5728\u7ed9\u81ea\u5df1\u538b\u529b\uff0c\u660e\u660e\u6211\u771f\u7684\u5f88\u60f3\u51fa\u53bb\u73a9\uff0c\u660e\u660e\u6211\u5f88\u60f3\u8c08\u604b\u7231\uff0c\u4f46\u662f\u6211\u8fd8\u662f\u5f88\u60f3\u53bb\u5b66\u4e60\u63d0\u5347\u81ea\u5df1\uff0c\u771f\u7684\u4e0d\u60f3\u518d\u540a\u513f\u90ce\u5f53\u7684\u4e86\u3002\\n\\n\u611f\u60c5\u65b9\u9762\uff0c\u6ca1\u94b1\u6ca1\u80fd\u529b\u53c8\u61d2\u4e0d\u60f3\u8ffd\uff0c\u8ffd\u4e86\u53c8\u4e0d\u73cd\u60dc\u559c\u6b22\u628a\u4eba\u5bb6\u5f53\u6811\u6d1e\uff0c\u9700\u6c42\u8fd9\u4e48\u9ad8\u4f60\u662f\u7532\u65b9\u8fd8\u662f\u7537\u670b\u53cb\uff1f\\n\\n> \u8981\u4e48\u6211\u70b8\uff0c\u8981\u4e48\u4f60\u70b8\\n\\n\u771f\u8ba8\u538c\\n\\n\u4eba\u7684\u56fa\u5b9a\u601d\u7ef4\u771f\u96be\u6539\uff0c\u597d\u7d2f\uff0c\u8bb2\u4e0d\u901a\u7684\uff0c\u4e0d\u60f3\u8ba9\u5927\u5bb6\u90fd\u597d\u7d2f\uff0c\u8c01\u4e0d\u559c\u6b22\u5927\u540c\u4e16\u754c\\n\\n\u5982\u679c\u4eba\u4eec\u90fd\u80fd\u591f\u5fc3\u610f\u76f8\u901a\uff0c\u8be5\u591a\u597d\u3002\\n\\n\u5982\u679c\u6211\u6709\u80fd\u529b\u8ba9\u5927\u5bb6\u90fd\u8212\u670d\uff0c\u8be5\u591a\u597d\u3002\\n\\n\u770b\u5b8c\u4e86\uff0c\u7ed9\u4f60\u4eec\u6574\u70b9\u597d\u73a9\u7684[\u563b\u563b](/somethingInterest) \u5982\u679c\u6253\u4e0d\u5f00\u8bb0\u5f97\u5237\u65b0\u4e00\u4e0b"},{"id":"Decomposition-of-mecanum-wheel-motion","metadata":{"permalink":"/Decomposition-of-mecanum-wheel-motion","editUrl":"https://github.com/pansyhou/pansyhou.github.io/edit/main/blog/develop/\u9ea6\u8f6e\u8fd0\u52a8\u5206\u89e3.md","source":"@site/blog/develop/\u9ea6\u8f6e\u8fd0\u52a8\u5206\u89e3.md","title":"\u9ea6\u514b\u7eb3\u59c6\u8f6e\u8fd0\u52a8\u5206\u89e3","description":"\u9ea6\u514b\u7eb3\u59c6\u8f6e\u8fd0\u52a8\u5206\u89e3\uff0c\u6bd4CSDN\u66f4\u52a0\u5feb\u901f\u4e0a\u624b","date":"2023-03-13T00:00:00.000Z","formattedDate":"2023\u5e743\u670813\u65e5","tags":[{"label":"\u9ea6\u514b\u7eb3\u59c6\u8f6e","permalink":"/tags/\u9ea6\u514b\u7eb3\u59c6\u8f6e"},{"label":"RoboMaster","permalink":"/tags/robo-master"}],"readingTime":1.3166666666666667,"hasTruncateMarker":true,"authors":[{"name":"pansyhou","title":"embedded engineer / \u5b66\u751f","url":"https://github.com/pansyhou","imageURL":"/img/logo.webp","key":"pansyhou"}],"frontMatter":{"slug":"Decomposition-of-mecanum-wheel-motion","title":"\u9ea6\u514b\u7eb3\u59c6\u8f6e\u8fd0\u52a8\u5206\u89e3","date":"2023-03-13T00:00:00.000Z","authors":"pansyhou","tags":["\u9ea6\u514b\u7eb3\u59c6\u8f6e","RoboMaster"],"keywords":["\u9ea6\u514b\u7eb3\u59c6\u8f6e","RoboMaster"]},"prevItem":{"title":"\u8d5b\u524d19\u5929\u778ebb","permalink":"/the-19days-before-robomaster"},"nextItem":{"title":"example.","permalink":"/why-i-dont-write-notes"}},"content":"\u9ea6\u514b\u7eb3\u59c6\u8f6e\u8fd0\u52a8\u5206\u89e3\uff0c\u6bd4CSDN\u66f4\u52a0\u5feb\u901f\u4e0a\u624b\\n\\n\x3c!-- truncate --\x3e\\n\\n```c\\n/************************** Dongguan-University of Technology -ACE**************************\\n * @brief \u5e95\u76d8\u72ec\u7acb\u6a21\u5f0f\uff0c\u53f3\u624b\u7cfb\uff0c\u98df\u6307X \uff0c\u4e2d\u6307Y\\n *\\n * @param C \\n * @param X_IN\\n * @param Y_IN\\n * @param Z_IN\\n * @param ExpRescue \\n ************************** Dongguan-University of Technology -ACE***************************/\\n// \u5c0f\u8f6e\u6cd5\u5411\u91cf\u5411\u4e2d\u5fc3\\n// 1,2\\n// 3,4\\nvoid Chassis_Indepen_Drive(C_t *C, float X_IN, float Y_IN, float Z_IN, int16_t ExpRescue) {\\n\\n    uint8_t i = 0;\\n    int16_t Val[4] = {0, 0, 0, 0}; //\u8bb0\u5f55\u7535\u673a\u5b9e\u65f6\u901f\u5ea6\ufffd?\\n    int16_t MAX = 0;               //\u56db\u4e2a\u7535\u673a\u91cc\u7684\ufffd?\u5927\ufffd?\ufffd\u5ea6\\n    float SPID_OUT[4];             // PIDout\\n\\n    if (Z_IN != 0)\\n        Z_IN = -Z_IN;\\n\\n    /*\u5e95\u76d8\u9ea6\u8f6e\u8fd0\u52a8\u5206\u89e3*/\\n    C->WheelMotor[0].ExpSpeed = -(X_IN + Y_IN - Z_IN);\\n    C->WheelMotor[1].ExpSpeed = (X_IN - Y_IN + Z_IN);\\n    C->WheelMotor[2].ExpSpeed = -(X_IN - Y_IN - Z_IN);\\n    C->WheelMotor[3].ExpSpeed = (X_IN + Y_IN + Z_IN);\\n\\n    /*\u901f\u5ea6\u589e\u76ca*/\\n    C->WheelMotor[0].ExpSpeed *= 10;\\n    C->WheelMotor[1].ExpSpeed *= 10;\\n    C->WheelMotor[2].ExpSpeed *= 10;\\n    C->WheelMotor[3].ExpSpeed *= 10;\\n\\n    /*PID\u5904\u7406*/\\n    for (i = 0; i < 4; i++) {\\n        PidCalculate(&C->WheelMotor[i].SPID, C->WheelMotor[i].ExpSpeed, C->WheelMotor[i].Encoder->Speed[1]); //\u5f97\u5230\u8f93\u51fa\ufffd?\\n        //        Val[i] = C->WheelMotor[i].Encoder->Speed[1];    //\u8bb0\u5f55\u7535\u673a\u5b9e\u65f6\u901f\u5ea6\ufffd?\\n    }\\n    \\n    CAN1_C620_OR_C610_201_TO_204_SendMsg(C->WheelMotor[0].SPID.out, C->WheelMotor[1].SPID.out,\\n                                         C->WheelMotor[2].SPID.out, C->WheelMotor[3].SPID.out);\\n\\n}\\n```\\n\\n\\n\\n![](https://pic2.imgdb.cn/item/644e1bbc0d2dde5777d42909.jpg)\\n\\n\u5148\u5047\u5b9a\u8fd9\u51e0\u4e2a\u8f6e\u5b50\u548c\u7535\u673a\u5728\u6570\u7ec4\u91cc\u7684\u7f16\u53f7\uff0c\u7535\u8c03ID\u5f80\u7f16\u53f7\u91cc+1\u5373\u53ef\u3002\\n\\n## \u5bf9\u7535\u673a\u8f6c\u901f\u4f5c\u51fa\u6b63\u8d1f\u533a\u5206\\n\\n```c\\n    C->WheelMotor[0].ExpSpeed = -(X_IN + Y_IN - Z_IN);\\n    C->WheelMotor[1].ExpSpeed = (X_IN - Y_IN + Z_IN);\\n    C->WheelMotor[2].ExpSpeed = -(X_IN - Y_IN - Z_IN);\\n    C->WheelMotor[3].ExpSpeed = (X_IN + Y_IN + Z_IN);\\n```\\n\\n\u53ef\u4ee5\u770b\u52300\u548c2\u53f7\u88ab\u52a0\u4e86\u4e2a\u8d1f\uff0c\u56e0\u4e3a\u4e0e1\u548c3\u7684\u8f6c\u52a8\u65b9\u5411\u4e0d\u4e00\u81f4\uff0c\u53d6\u53cd\u5373\u53ef\u3002\\n\\n:::caution\\n\\n\u5982\u679c\u60f3\u8bbe\u7f6e\u4e0d\u540c\u7684ID\uff0c\u6bd4\u5982\u8bf4\u987a\u65f6\u9488\u6216\u8005\u9006\u65f6\u9488\u6bd4\u8f83\u597d\u8bb0\uff0c\u90a3\u5c31\u6539\u53d8\u4ed6\u8fd9\u4e2a\u8d1f\u53f7\u6539\u53d8\u901f\u5ea6\u65b9\u5411\u5373\u53ef\u3002\\n\\n:::\\n\\n## \u5bf9\u8f93\u5165\u91cf\u8fdb\u884c\u9ea6\u8f6e\u8fd0\u52a8\u5206\u89e3\\n\\n![](https://pic2.imgdb.cn/item/644e1c780d2dde5777d4bb90.jpg)\\n\\n\u5c31\u80fd\u5f97\u5230\u4e0a\u8ff0\u516c\u5f0f"},{"id":"why-i-dont-write-notes","metadata":{"permalink":"/why-i-dont-write-notes","editUrl":"https://github.com/pansyhou/pansyhou.github.io/edit/main/blog/lifestyle/\u8ddd\u79bb\u6211\u4e0a\u7bc7\u7b14\u8bb0\u8fd8\u662f\u5728.md","source":"@site/blog/lifestyle/\u8ddd\u79bb\u6211\u4e0a\u7bc7\u7b14\u8bb0\u8fd8\u662f\u5728.md","title":"example.","description":"example.","date":"2023-03-13T00:00:00.000Z","formattedDate":"2023\u5e743\u670813\u65e5","tags":[{"label":"\u968f\u7b14","permalink":"/tags/\u968f\u7b14"},{"label":"\u6742\u8c08","permalink":"/tags/\u6742\u8c08"}],"readingTime":0.0033333333333333335,"hasTruncateMarker":false,"authors":[{"name":"pansyhou","title":"embedded engineer / \u5b66\u751f","url":"https://github.com/pansyhou","imageURL":"/img/logo.webp","key":"pansyhou"}],"frontMatter":{"slug":"why-i-dont-write-notes","title":"example.","date":"2023-03-13T00:00:00.000Z","authors":"pansyhou","tags":["\u968f\u7b14","\u6742\u8c08"],"keywords":["\u968f\u7b14","\u6742\u8c08"]},"prevItem":{"title":"\u9ea6\u514b\u7eb3\u59c6\u8f6e\u8fd0\u52a8\u5206\u89e3","permalink":"/Decomposition-of-mecanum-wheel-motion"}},"content":"example."}]}')}}]);